import { createMuiTheme } from '@material-ui/core/styles';
import blue from '@material-ui/core/colors/blue';

const theme = createMuiTheme({
    palette: {
        primary: blue,
    },
    overrides: {
        MuiAppBar: {

        },
        MuiDrawer: {
            paper: {
                width: "60vw"
            }
        },
        MuiList: {
            root: {
                textDecoration: "none",
                color: 'primary',
            }
        }
  },
});

export default theme;
