import React, { useState } from 'react'
import { useHistory } from "react-router-dom";

import AppBar from '@material-ui/core/AppBar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Toolbar from '@material-ui/core/Toolbar';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Hidden from '@material-ui/core/Hidden';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import MenuIcon from '@material-ui/icons/Menu';
import { Link } from 'react-router-dom';
import QuiSommesNousPage from '../../../pages/QuiSommesNousPage';
import './style.css';
import { getRoutePathByName } from '../../../routes/index';

// const links = [
//     {
//         name: "Qui sommes nous ?",
//         routeName: "/qui-sommes-nous",
//     },
//     {
//         name: "Transformation digitale",
//         routeName: "/transformation-digitale"
//     },
//     {
//         name: "Conseils",
//         routeName: "/conseils"
//     },
//     {
//         name: "Social Marketing",
//         routeName: "/social-marketing"
//     },
//     {
//         name: "Budget",
//         routeName: "/budget"
//     }
// ];

const NavBar = () => {
    const [open, setOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState(0);
    let history = useHistory();

    const handleChange = (event, newValue) => {
        setSelectedItem(newValue);
    };

    const toggleDrawer = (status) => {
        setOpen(status);
    };

    return (
        <>
            <AppBar position="static">
                <Toolbar>
                    <IconButton 
                        edge="start"
                        color="inherit"
                        aria-label="menu"
                        className="iconMenu"
                        onClick={() => toggleDrawer(true)} >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6">Spotline</Typography>

                    <Hidden mdUp>   
                        <Drawer 
                            open={open}
                            onClose={() => toggleDrawer(false)}
                        >
                            <List>
                                <Tabs 
                                    value={selectedItem} 
                                    onChange={handleChange}
                                    orientation="vertical"
                                >
                                    {links && links.map((link) => (
                                        <Tab label={link.name} onClick={() => toggleDrawer(false)}/>
                                    ))}
                                </Tabs>
                            </List>
                        </Drawer>
                    </Hidden>


                    <Hidden only={['sm', 'xs']}>                  
                        <div className="sideLinks">
                        <Tabs value={selectedItem} onChange={handleChange}>
                            { links && links.map((link) => (
                                <Tab label={link.name}/>
                            ))}
                        </Tabs>
                        </div>
                    </Hidden>
                </Toolbar>
            </AppBar>
            { history.push(links[selectedItem].routeName)}
        </>
    );
}

export default NavBar;
